import React, { useContext, useState } from 'react'
import data from './data'


const Person= React.createContext()


 const PropDrilling = () => {
    const [people, setPeople] = useState(data)
    const removePerson = (id) => {
            setPeople((people)=>{
                return people.filter((person)=> person.id !==id)
                })
            }
           return (
        <Person.Provider value={{removePerson}} className='section'>
            <h1>context api</h1>
            <List people={people} />
        </Person.Provider>
  )
}
  const List = ({people, removePerson}) => {
    return <>
    {people.map((person)=>{
        return(
            <SinglePerson key={person.id} {...person} removePerson={removePerson}/>
        )
    })}
    </>
  }
  const SinglePerson = ({id, name}) => {
    const {removePerson} = useContext(Person)

    return(
        <div >
            <h2>{name}</h2>
            <button className='btn' onClick={()=>removePerson(id)}>remove</button>
        </div>
    )
  }

export default PropDrilling
