import React, { useState } from 'react'

const List = ({people}) => {
    const [readmore, setReadmore] = useState(false)
  return <>
  {people.map(({name, age, id, desc, removeId}) =>{
   
    return <article key={id}>
<div>
    <h3>{name}</h3>
    <p>{age}years</p>
    <p>
        {readmore ? desc : `${desc.substring(0, 500)}...`}
    </p>
    <button onClick={()=> setReadmore(!readmore)}>
        {readmore ? "showless" : "readmore" }
    </button>
    <button  onClick={()=> removeId(i)}>not interested</button>
</div>
    </article>
  })}
  </>
   
}

export default List
